body {
  font: 18px 'Helvetica Neue', Helvetica, Arial;
}

fieldset {
  border: 0;
}

input {
  vertical-align: middle;
}

input[type=text] {
  font: 18px 'Helvetica Neue', Helvetica, Arial;
  width: 20em;
}

.text[disabled] {
  opacity: 0.3;
}

label[for] {
  padding-left: 0.3em;
}

input[disabled] + label {
  opacity: 0.3;
}

