'use strict';


var formDeps = {
  name: 'v1',
  children: [
    {
      name: 'v2',
      children: null
    }, 
    {
      name: 'v3',
      children: null
    }, 
    {
      name: 'v4',
      children: [
        {
          name: 'v5',
          children: null
        }
      ]
    }
  ]
};


var preorder = function(node, parent) {
  var formElement = document.forms[0][node.name];
  var parentElement = parent ? document.forms[0][parent.name] : null;

  parent && (formElement.disabled = !parentElement.checked || parentElement.disabled);

  if (node.children) {
    node.children.forEach(function(childNode) {
      preorder(childNode, node);
    });
  }
};

