editors.js:
'use strict';


var formDeps = new Map([
  [document.forms[0]['v1'], [
    document.forms[0]['v2'],
    document.forms[0]['v3'],
    document.forms[0]['v4']
  ]],

  [document.forms[0]['v2'], null],

  [document.forms[0]['v3'], null],

  [document.forms[0]['v4'], [
    document.forms[0]['v5']
  ]],

  [document.forms[0]['v5'], null]
]);

var preorder = function(node, parent) {
  parent && (node.disabled = !parent.checked || parent.disabled);

  if (formDeps.get(node)) {
    formDeps.get(node).forEach(function(child) {
      preorder(child, node);
    });
  }
};


preorder(document.forms[0]['v1'], null)


document.forms[0].onchange = function(evt) {
  preorder(evt.target, null);
};

